body.rcode:
h <- function() {
    with(datasets::mtcars,
         plot(lm(mpg ~ disp)))
}
gdiffOutput(h, "Control")

fungen <- function(x) {
    function() plot(x)
}
k <- fungen(1:10)
gdiffOutput(k, "Control")

x <- c(0, 0.5, 1, 0.5)
y <- c(0.5, 1, 0.5, 0)
f1 <- function() { grid::grid.bezier(x, y) }
f2 <- function() { gridBezier::grid.Bezier(x, y) }
gdiff(list(control=f1, test=f2), name="bezier")
